# https://www.hackerrank.com/challenges/compress-the-string/problem?isFullScreen=true

from itertools import groupby

s = ""

lst = list(groupby(s))
n_lst, uk = list(), list()
for k, g in groupby(s):
    n_lst.append(len(list(g)))
    uk.append(int(k))

for i, j in zip(n_lst, uk):
    print(f"({i}, {j})", end=" ")
